#34;50%&#34;&gt;&lt;/mtd&gt;&lt;mtd&gt;&lt;mtext&gt;(2.8)&lt;/mtext&gt;&lt;/mtd&gt;&lt;/mtr&gt;&lt;/mtable&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;\begin{cases}
\delta_1 = l_1-L_0 = l_1 - \bar{L} + \bar{L} - L_0 = \nu_1+\bar{\delta} \\
\delta_2 = l_2-L_0 = l_2 - \bar{L} + \bar{L} - L_0 = \nu_2+\bar{\delta} \\
\vdots \\ 
\delta_n = l_n-L_0 = l_n - \bar{L} + \bar{L} - L_0 = \nu_n+\bar{\delta} 
\end{cases} 
\tag{2.8}
