gt;&lt;/mstyle&gt;&lt;/mtd&gt;&lt;/mtr&gt;&lt;/mtable&gt;&lt;/mtd&gt;&lt;mtd width=&#34;50%&#34;&gt;&lt;/mtd&gt;&lt;mtd&gt;&lt;mtext&gt;(3)&lt;/mtext&gt;&lt;/mtd&gt;&lt;/mtr&gt;&lt;/mtable&gt;&lt;annotation encoding=&#34;application/x-tex&#34;&gt;\begin{aligned}
E(t) =&amp;amp; \sum_{q=-N}^{N} E_0 \cos[(\omega_c+q\Delta\omega)t+q\alpha] \\
=&amp;amp; E_0 \cos(\omega_ct) + E_0 \bigg[ \cos(\omega_ct+\Delta\omega t+\alpha) + \cos(\omega_ct-\Delta\omega t-\alpha) \bigg]  \\
&amp;amp;\quad+ E_0 \bigg[ \cos(\omega_ct+2\Delta\omega t+2\alpha) + \cos(\omega_ct-2\Delta\omega t-2\alpha) \bigg] \\
&amp;amp;\quad+\  \cdots \\
&amp;amp;\quad+ E_0 \bigg[ \cos(\omega_ct+N\Delta\omega t+N\alpha) + \cos(\omega_ct-N\Delta\omega t-N\alpha) \bigg] \\
=&amp;amp; E_0 \cos(\omega_ct) + 2E_0 \cos(\omega_ct) \cos(\Delta\omega t+\alpha) + \cdots + 2E_0 \cos(\omega_ct) \cos\left[N(\Delta\omega t+\alpha)\right] \\
=&amp;amp; E_0\cos(\omega_ct) \left[ 1+2\sum_{q=1}^N \cos[q(\Delta\omega t+\alpha)] \right] \\
=&amp;amp; E_0\cos(\omega_ct) \left[ 1+2\frac{\sin\left(\frac{1}{2}N\beta\right)\cos\left[\frac{1}{2}(N+1)\beta\right]}{\sin\left(\frac{1}{2}\beta\right)} \right] \textcolor{red}{\quad(设\beta=\Delta\omega t+\alpha)} \\
=&amp;amp; E_0\cos(\omega_ct) \left[ \frac{\cos(\frac{1}{2}N\beta)\sin\left[\frac{1}{2}(N+1)\beta\right]-\sin(\frac{1}{2}N\beta)\cos\left[\frac{1}{2}(N+1)\beta\right]}{\sin\left(\frac{1}{2}\beta\right)} + 2\frac{\sin\left(\frac{1}{2}N\beta\right)\cos\left[\frac{1}{2}(N+1)\beta\right]}{\sin\left(\frac{1}{2}\beta\right)} \right] \\
=&amp;amp; E_0\cos(\omega_ct)\left[ \frac{\cos(\frac{1}{2}N\beta)\sin\left[\frac{1}{2}(N+1)\beta\right]+\sin(\frac{1}{2}N\beta)\cos\left[\frac{1}{2}(N+1)\beta\right]}{\sin\left(\frac{1}{2}\beta\right)} \right] \\
=&amp;amp; E_0\cos(\omega_ct) \frac{\sin\left[\frac{1}{2}(2N+1)(\Delta\omega t+\alpha)\right]}{\sin\left[\frac{1}{2}(\Delta\omega t+\alpha)\right]}
\end{aligned}
