coding=&#34;application/x-tex&#34;&gt;\begin{aligned}
\epsilon &amp;amp;= A\sqrt{P_cP_s}\cos(\varphi-\phi) = A\sqrt{P_cP_s}\left(\cos\varphi\cos\phi+\sin\varphi\sin\phi\right) \\
&amp;amp;= A\sqrt{P_cP_s} \left(\frac{\mathrm{Re}\left[F(\omega)F^*(\omega+\Omega)-F^*(\omega)F(\omega-\Omega)\right]}{A}\sin\phi-\frac{\mathrm{Im}\left[F(\omega)F^*(\omega+\Omega)-F^*(\omega)F(\omega-\Omega)\right]}{A}\cos\phi\right) \\
&amp;amp;= \sqrt{P_cP_s}\left({\mathrm{Re}\left[F(\omega)F^*(\omega+\Omega)-F^*(\omega)F(\omega-\Omega)\right]}\sin\phi-{\mathrm{Im}\left[F(\omega)F^*(\omega+\Omega)-F^*(\omega)F(\omega-\Omega)\right]}\cos\phi\right)
\end{aligned}
